import React, { useState , useEffect } from 'react';
import './App.css';
import { BrowserRouter } from 'react-router-dom';
import NavBar from './NavBar';
import Routes from './Routes';
import JoblyApi from './api';

function App() {
  const emptyUserData = {username: '', firstName: '', lastName: '', email: '', isAdmin: '', applications: []};
  const [ companies, setCompanies ] = useState([]);
  const [ jobs, setJobs ] = useState([]);
  const [ userToken, setUserToken ] = useState('');
  const [ user, setUser ] = useState( emptyUserData );
  const [ isLoggedIn, setIsLoggedIn ] = useState(false);
  // Combine state for userToken and user by putting the token in the user info
  const [ formData, setFormData ] = useState({ username: '', password: ''});
  const [ validCredentials, setValidCredentials ] = useState(true);
  const [ signupFormData, setSignupFormData ] = useState( emptyUserData );
  const [ errorMessage, setErrorMessage ] = useState('');

  useEffect( () => {
    const getCompanies = async () => {
      const allCompanies = await JoblyApi.getAllCompanies();
      setCompanies(allCompanies);
    }

    const getJobs = async () => {
      const allJobs = await JoblyApi.getAllJobs();
      setJobs(allJobs);
    }

    getCompanies(); // await for these 2??
    getJobs();
  }, []);


  useEffect( () => {
    const logout = () => {
      setUserToken('');
      setUser({username:'', firstName:'', lastName:'', email:'', isAdmin:'', applications:[]});
    };

    if (!isLoggedIn) logout();
  }, [isLoggedIn]);



  const setUserInfo = user => {
    setUser({
      username:     user.username,
      firstName:    user.firstName,
      lastName:     user.lastName,
      email:        user.email,
      isAdmin:      user.isAdmin,
      applications: user.applications
    });
  }

  //  Refactor the 4 functions below into 2, pass the type of form data aswell as
  //  the api method as a string. Once you've done this and made sure everything
  //  works, see if you can refactor the 2 to work in a useEffect hook.
  // 
  //  DO THIS BEFORE MOVING ON TO STEP 7

  const handleFormChange = (evt, login=false, signup=false) => {
    const { name, value } = evt.target;
    if (login) setFormData( data => ({...data, [name]: value}));
    else setSignupFormData(data => ({...data, [name]: value}))
  };

  const resetFormData = (login, signup) => {
    if(login) setFormData( { username: '', password: ''} );
    else setSignupFormData( {username: '', password: '', firstName:'', lastName: '', email: ''});
  };

  const handleFormSubmit = async (evt, apiMethod, formType, login=false, signup=false) => {
    evt.preventDefault();
    try {
      let token = await JoblyApi[ [apiMethod] ](formType);
      setUserToken(token);
      setValidCredentials(true);

      const user_ = await JoblyApi.getUser(formType.username);
      setUserInfo(user_);
      resetFormData(login, signup);
      setErrorMessage(''); // probalbly won't need it here..
      setIsLoggedIn(true);
    }catch(err) {
      if(login) setValidCredentials(false);
      else setErrorMessage(err);
    }
  };

  // const handleSignupSubmit = async evt => {
  //   evt.preventDefault();
  //   try {
  //     const token = await JoblyApi.signup(signupFormData);
  //     setUserToken(token);
  //     setValidCredentials(true);

  //     const user_ = await JoblyApi.getUser(signupFormData.username);
  //     setUserInfo(user_);
  //     setSignupFormData( {username: '', password: '', firstName:'', lastName: '', email: ''});// May not need this, double check
  //     setErrorMessage('');
  //     setIsLoggedIn(true);
  //   }catch(err) {
  //     setErrorMessage(err);  // setErrorMessage or setValidCredentials can be determined in the merge by checking: Object(funcParam).length > 2 
  //   }
  // };

  // const handleLoginSubmit = async evt => {
  //   evt.preventDefault();
  //   try {
  //     let token = await JoblyApi.logIn(formData);
  //     setUserToken(token);
  //     setValidCredentials(true);

  //     const user = await JoblyApi.getUser(formData.username);
  //     setUserInfo(user);
  //     setFormData({ username: '', password: ''}); // May not need this, double check
  //     setErrorMessage(''); // probalbly won't need it here..
  //     setIsLoggedIn(true);
  //   }catch(err) {
  //     setValidCredentials(false);
  //   }
  // };


  return (
    <div className="App">
      <BrowserRouter>
        <NavBar 
          userToken={userToken} 
          setIsLoggedIn={setIsLoggedIn}
          user={user} 
        />
        <main>
          <Routes 
            companies={companies} 
            jobs={jobs} 
            handleFormChange={handleFormChange}
            handleFormSubmit={handleFormSubmit}
            userToken={userToken}
            user={user}
            formData={formData}
            validCredentials={validCredentials}
            signupFormData={signupFormData}
            errorMessage={errorMessage}
          />
        </main>
      </BrowserRouter>
    </div>
  );
};

export default App;
