import React from 'react';
import { useHistory } from 'react-router-dom';
import { Form, FormGroup, Label, Input, Button } from 'reactstrap';
import './Signup.css';


const Signup = ({ signupFormData, handleFormChange, handleSignupSubmit, errorMessage, userToken }) => {
  const history = useHistory();
  if ( !errorMessage && userToken ) history.push('/');

  const handleSignupChange = evt => handleFormChange(evt, false, true);

  return (
    <div className='signup-form-div'>
      <h2 className='signup-message'>Sign Up</h2>
      <Form onSubmit={handleSignupSubmit} className='signup-form'>
        <FormGroup>
          <Label for='username'>Username</Label>
            <Input 
              autoFocus
              type='text'
              name='username'
              id='username'
              value={signupFormData.username}
              onChange={handleSignupChange}  
            />
        </FormGroup>
        <FormGroup>
          <Label for='password'>Password</Label>
            <Input 
              type='password'
              name='password'
              id='password'
              value={signupFormData.password}
              onChange={handleSignupChange}  
          />
        </FormGroup>
        <FormGroup>
          <Label for='firstName'>First Name</Label>
            <Input  
              type='text'
              name='firstName'
              id='firstName'
              value={signupFormData.firstName}
              onChange={handleSignupChange}
          />
        </FormGroup>
        <FormGroup>
          <Label for='lastName'>Last Name</Label>
            <Input 
              type='text'
              name='lastName'
              id='lastName'
              value={signupFormData.lastName}
              onChange={handleSignupChange}  
          />
        </FormGroup>
        <FormGroup>
          <Label for='email'>Email</Label>
            <Input 
              type='email'
              name='email'
              id='email'
              value={signupFormData.email}
              onChange={handleSignupChange}
            />
        </FormGroup>
        { errorMessage && 
          <div className='bad-signup-div'>
            <p className='bad-signup-message'>{errorMessage}</p>
          </div>      
        }
        <Button color='primary' className='signup-form-button'>Sign Up</Button>
      </Form>
    </div>  
  );  
}

export default Signup;
