import React from 'react';
import './JobList.css';
import Job from './Job';

const JobList = ({ jobs }) => {

  const [ formData, setFormData ] = useState({ title: ''});
  const [ jobList, setJobList ] = useState(jobs);
  const [ isEmpty, setIsEmpty ] = useState(false);

  const handleChange = evt => {
    const { name, value } = evt.target;  
    setFormData( data => ({...data, [name]: value}));
  };

  const handleSubmit = async evt => {
    evt.preventDefault();  
    const res = formData.name !== '' ?
      await JoblyApi.getAllCompanies(formData)
      : companies;
    setIsEmpty( res.length === 0 ? true: false); 
    setCompanyList(res);
  };

  return (
    <div>
      <div className='job-form-div'>
        <form onSubmit={handleSubmit}>
          <label htmlFor='job-title-search' />
          <input 
            className='job-title-search-input'
            id='job-title-search'
            name='title'
            placeholder='Enter a serch term...'
            value={formData.title}
            onChange={handleChange}
          />
          <Button className='job-title-search-button' color='primary'>Submit</Button>    
        </form>
      </div>    
      {jobs.map( job => (
        <Job 
          key={job.id}
          id={job.id}
          title={job.title}
          companyHandle={job.companyHandle}
          salary={job.salary}
          equity={job.equity}
        />
      ))}  
    </div>  
  );
};

export default JobList;
