      await JoblyApi.getAllCompanies(formData)
      : companies;
    setIsEmpty( res.length === 0 ? true: false); 
    setCompanyList(res);
  };

  return (
    <div>
      {jobs.map( job => (
        <Job 
          key={job.id}
          id={job.id}
